import React, { Component } from 'react';
import { Modal, Button, Row, Col, Form } from 'react-bootstrap';

export class AddDepModal extends Component {
    constructor(props) {
        super(props);
    }

    handleSubmit(event){
        event.preventDefault();
        console.log(event.target);
        console.log(event.target.DepartmentName);
        // alert(event.target.DepartmentName.value);
        fetch("https://localhost:44381/api/Department",{
            method:"POST",
            headers:{
                "Accept": "application/json",
                "Content-Type": "application/json"
            },
            body:JSON.stringify({
                DepartmentID: null,
                DepartmentName:event.target.DepartmentName.value
            })
        })
        .then(response=> response.json())
        .then((result)=>{
            alert(result);
        },
        (error)=>{
            alert('Failed');
        })
    }

    render() {
        return (

            <Modal
                {...this.props}
                size="lg"
                aria-labelledby="contained-modal-title-vcenter"
                centered>
                <Modal.Header closeButton>
                    <Modal.Title id="contained-modal-title-vcenter">
                        Add Department
                </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <div className="container">
                    <Row>
                        <Col sm={6}>
                        <Form onSubmit={this.handleSubmit}>
                        <Form.Group controlId="DepartmentName">
                            <Form.Label>Department Name</Form.Label>
                            <Form.Control type="text" name="DepartmentName" required placeholder="DepartmentName" />
                        </Form.Group>
                        <Form.Group>
                            <Button variant="primary" type="submit">Add Department</Button>
                        </Form.Group>

                        </Form>
                        </Col>
                    </Row>
                    </div>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="danger" onClick={this.props.onHide}>Close</Button>
                </Modal.Footer>
            </Modal>
        );

    }
}
